
CREATE TABLE Users (
    userId INTEGER PRIMARY KEY AUTOINCREMENT,
    username TEXT UNIQUE NOT NULL,
    firstName TEXT NOT NULL,
    lastName TEXT NOT NULL,
    encryptedPassword TEXT NOT NULL,
    encryptedPasswordSalt TEXT NOT NULL
);

